import React from "react";
import { motion } from "framer-motion";

const SelectedCard = ({ selected, onClose }) => {
  return (
    <motion.div
      className="fixed inset-0 flex justify-center items-center z-50"
      initial={{ opacity: 0, scale: 0.8 }}
      animate={{ opacity: 1, scale: 1 }}
      exit={{ opacity: 0, scale: 0.8 }}
      onClick={onClose} // Close when clicking anywhere on the card
    >
      <div
        className="relative bg-white rounded-lg p-4 overflow-hidden"
        style={{
          width: "auto", // Maintain original width
          height: "auto", // Maintain original height
          maxWidth: "90%", // Prevent the card from being too large on smaller screens
        }}
      >
        <motion.img
          src={selected.thumbnail}
          alt="thumbnail"
          className="object-cover w-full h-full"
        />
        <div className="absolute bottom-0 left-0 p-4 text-white bg-black bg-opacity-50 w-full">
          {selected.content}
        </div>
      </div>
    </motion.div>
  );
};

export default SelectedCard;
